mapper.select:
SELECT s.ID as id, s.NAME as name, s.TYPE as type, s.LINK as link, s.ICON as icon, s.PARENT_ID as parentId, s.FLAG as flag, s.MEMO as memo, s.STATE as state, s.ADD_TIME as addTime, s.ADD_USER_ID as addUserId, s.MOD_TIME as modTime, s.MOD_USER_ID as modUserId, s.UPDATE_LOCK as updateLock FROM SYS_T_RESOURCE s WHERE 1 = 1
and s.name like "%" + #{name} + "%"
and s.flag = #{flag}
and s.type = #{type}
ORDER BY s.MOD_TIME DESC

mapper.delete:
		DELETE FROM SYS_T_RESOURCE
		WHERE id in 
          
        	#{ids}
