mapper.select:
SELECT s.ID as id, s.TYPE as type, s.NAME name, s.MEMO as memo, s.STATE as state, s.COMPANY_ID as companyId, c.NAME as companyName, s.ADD_TIME as addTime, s.ADD_USER_ID as addUserId, s.MOD_TIME as modTime, s.MOD_USER_ID as modUserId, s.UPDATE_LOCK as updateLock FROM AUTHZ_T_PERMISSION s LEFT JOIN ORG_T_COMPANY c on s.COMPANY_ID = c.ID WHERE 1 = 1
and s.COMPANY_ID = #{companyId}
ORDER BY s.MOD_TIME DESC

mapper.delete:
		DELETE FROM AUTHZ_T_PERMISSION
		WHERE id in 
          
        	#{ids}
